pragma solidity ^0.5.0;

contract AcademicCredentialVerifier {
    struct AcademicCredential {
        string degreeType;
        string institutionName;
        uint256 dateOfIssuance;
    }

    mapping(address => AcademicCredential) public academicCredentials;

    function addAcademicCredential(string memory _degreeType, string memory _institutionName, uint256 _dateOfIssuance) public {
        AcademicCredential memory newCredential = AcademicCredential(_degreeType, _institutionName, _dateOfIssuance);
        academicCredentials[msg.sender] = newCredential;
    }

    function getAcademicCredential(address _address) public view returns (string memory, string memory, uint256) {
        AcademicCredential memory credential = academicCredentials[_address];
        return (credential.degreeType, credential.institutionName, credential.dateOfIssuance);
    }

    function verifyAcademicCredential(address _address) public view returns (bool) {
        return academicCredentials[_address].dateOfIssuance != 0;
    }
}
